(23, 'opencart 数据字典', 'opencart-', '', '<h2>Opencart数据字典</h2>\r\n<hr />\r\n<h3>1.Address 表</h3>\r\n<p>地址表</p>\r\n<p>用于存放会员的地址信息</p>\r\n<p>主键：address_id</p>\r\n<table class="tbl" border="0">\r\n<tbody>\r\n<tr>\r\n<th style="width: 90px;">字段名</th><th style="width: 84px;">数据类型</th><th style="width: 76px;">长度</th><th style="width: 78px;">是否为空</th><th style="width: 38px;">主键</th><th style="width: 226px;">备注</th>\r\n</tr>\r\n<tr>\r\n<th>address_id</th>\r\n<td>Int</td>\r\n<td>11</td>\r\n<td>Not</td>\r\n<td>Yes</td>\r\n<td>地址主键（自动增长）</td>\r\n</tr>\r\n<tr>\r\n<th>customer_id</th>\r\n<td>Int</td>\r\n<td>11</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>会员的注册ID(与customer表的主键关联)</td>\r\n</tr>\r\n<tr>\r\n<th>Company</th>\r\n<td>Varchar</td>\r\n<td>32</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>公司</td>\r\n</tr>\r\n<tr>\r\n<th>FirstName</th>\r\n<td>Varchar</td>\r\n<td>32</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>姓</td>\r\n</tr>\r\n<tr>\r\n<th>LastnNme</th>\r\n<td>Varchar</td>\r\n<td>32</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>名</td>\r\n</tr>\r\n<tr>\r\n<th>address_1</th>\r\n<td>Varchar</td>\r\n<td>32</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>街道地址</td>\r\n</tr>\r\n<tr>\r\n<th>address_2</th>\r\n<td>Varchar</td>\r\n<td>32</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>可随意填写</td>\r\n</tr>\r\n<tr>\r\n<th>postcode</th>\r\n<td>Varchar</td>\r\n<td>32</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>邮编</td>\r\n</tr>\r\n<tr>\r\n<th>city</th>\r\n<td>Varchar</td>\r\n<td>32</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>城市</td>\r\n</tr>\r\n<tr>\r\n<th>country_id</th>\r\n<td>Int</td>\r\n<td>11</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>国家编号(iso)（关联counry表的主键 country_id）</td>\r\n</tr>\r\n<tr>\r\n<th>Zone_id</th>\r\n<td>Int</td>\r\n<td>11</td>\r\n<td>Not</td>\r\n<td></td>\r\n<td>地区编号(iso)关联zone表的主键zone_id</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<hr />\r\n<h3>2. category 表</h3>\r\n<p>产品分类表：用于商品的多级分类</p>\r\n<p>主键：category_id</p>\r\n<table class="tbl" border="0">\r\n<tbody>\r\n<tr>\r\n<th style="width: 90px;">字段名</th><th style="wi