, col:focus, colgroup:focus, dd:focus,\r\ndel:focus, dfn:focus, dir:focus, div:focus, dl:focus, dt:focus, em:focus,\r\nfieldset:focus, font:focus, form:focus, h1:focus, h2:focus, h3:focus, h4:focus,\r\nh5:focus, h6:focus, hr:focus, i:focus, img:focus, ins:focus, \r\nkbd:focus, label:focus, legend:focus, li:focus, link:focus, menu:focus, \r\nol:focus, p:focus, pre:focus, q:focus, s:focus, samp:focus, \r\nsmall:focus, span:focus, strike:focus, strong:focus, sub:focus, sup:focus, \r\ntable:focus, tbody:focus, td:focus, tfoot:focus, th:focus, thead:focus, \r\ntr:focus, tt:focus, u:focus, ul:focus, var:focus {\r\n  /* Don''t specify the outline-color, we should always use initial value. */\r\n   outline: 1px dotted;\r\n}\r\n\r\n/* hidden elements */\r\narea, base, basefont, head, meta, script, style, title,\r\nnoembed, param {\r\n   display: none;\r\n}\r\n\r\n/* media elements */\r\nvideo &gt; xul|videocontrols, audio &gt; xul|videocontrols {\r\n  display: -moz-box;\r\n  -moz-box-orient: vertical;\r\n  -moz-binding: url("chrome://global/content/bindings/videocontrols.xml#videoControls");\r\n}\r\n\r\nvideo:not([controls]) &gt; xul|videocontrols,\r\naudio:not([controls]) &gt; xul|videocontrols {\r\n  visibility: hidden;\r\n}\r\n\r\naudio:not([controls]) {\r\n  display: none;\r\n}\r\n\r\n/* emulation of non-standard HTML &amp;lt;marquee&amp;gt; tag */\r\nmarquee {\r\n  width: -moz-available;\r\n  display: inline-block;\r\n  vertical-align: text-bottom;\r\n  text-align: start;\r\n  -moz-binding: url(''chrome://xbl-marquee/content/xbl-marquee.xml#marquee-horizontal'');\r\n}\r\n\r\nmarquee[direction="up"], marquee[direction="down"] {\r\n  -moz-binding: url(''chrome://xbl-marquee/content/xbl-marquee.xml#marquee-vertical'');\r\n  height: 200px;\r\n}\r\n\r\n/* PRINT ONLY rules follow */\r\n@media print {\r\n\r\n  marquee { -moz-binding: none; }\r\n\r\n}\r\n</pre>', '', 1, 7, 0, 16, '2012-03-06 14:29:48', 1, '', '2012-03-06 14:29:48', 62, 0, '0000-00-00 00:00:00', '2012-03-06 14:2