(3, 1, '', 'Joomla!', 'joomla', 0, 141, 0, '', 'http://www.joomla.org', '2009-10-15 07:56:43', 1, 0, '0000-00-00 00:00:00', '', '<a href="{CLICKURL}" target="_blank">{NAME}</a>\r\n<br/>\r\nJoomla!世上最热门且广泛使用的开源CMS项目。', 14, '', 0, 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', 'width=0\nheight=0'),
(4, 1, '', 'JoomlaCode', 'joomlacode', 0, 140, 0, '', 'http://joomlacode.org', '2009-10-14 01:05:07', 1, 0, '0000-00-00 00:00:00', '', '<a href="{CLICKURL}" target="_blank">{NAME}</a>\r\n<br/>\r\nJoomlaCode 简化程序开发和发布。', 14, '', 0, 2, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', 'width=0\nheight=0'),
