(6, 1, '', 'Joomla!商店', 'joomla-shop', 0, 136, 0, '', 'http://shop.joomla.org', '2009-10-14 01:20:59', 1, 0, '0000-00-00 00:00:00', '', '<a href="{CLICKURL}" target="_blank">{NAME}</a>\r\n<br/>\r\n全部Joomla!商品任您挑。', 14, '', 0, 4, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', 'width=0\nheight=0'),
(7, 1, '', 'Joomla!促销商店', 'joomla-promo-shop', 0, 310, 1, 'shop-ad.jpg', 'http://shop.joomla.org', '2009-10-14 01:17:03', 1, 0, '0000-00-00 00:00:00', '', '', 33, '', 0, 3, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', 'width=0\nheight=0'),
