(2, 0, 'Joomla!特别链接', '', 'joomla-specific-links', 'clock.jpg', 'com_weblinks', 'left', '<p>一份有关于Joomla!项目之链接的选集。</p>', 1, 0, '0000-00-00 00:00:00', NULL, 1, 0, 0, ''),
(3, 0, '新闻快报', '', 'newsflash', '', '1', 'left', '', 1, 0, '0000-00-00 00:00:00', '', 2, 0, 0, ''),
(4, 0, 'Joomla!', '', 'joomla', '', 'com_newsfeeds', 'left', '', 1, 0, '0000-00-00 00:00:00', NULL, 2, 0, 0, ''),
(5, 0, '自由与开源软件', '', 'free-and-open-source-software', '', 'com_newsfeeds', 'left', '<p>阅读来自一些领导倡导者，关于自由与开源软件的最新消息。</p>', 1, 0, '0000-00-00 00:00:00', NULL, 3, 0, 0, ''),
